import pandas as pd

#importo el CSV
df = pd.read_csv('EVSales.csv')
print("csv importado ")
print(df.head())

#filas y columnas del dataset

df.shape

#primeras 5 filas

df.head()

#limpieza basica datos nulos, cuantos por columnas
df.isnull().sum()

#Filtrado simple
#marca tesla y carga rapida

import pandas as pd
tesla_carga_rapida= df[(df['Brand'] == 'Tesla') & (df['Fast_Charging_Option'] == 'Yes')]
print(tesla_carga_rapida)

print (f"cantidad de vehiculos tesla con carga rapida: {len(tesla_carga_rapida)}")

#Agrupación y agregación
#Cuál es el promedio de ingresos (Revenue) por región

promedioRevenue = df.groupby('Region')['Revenue'].mean().apply(lambda x: round(x,2))
print(promedioRevenue)

#Muestra los resultados en un gráfico de barras.
promedioRevenue.plot(kind='bar')

#Visualización básica
#histograma de la columna Battery_Capacity_kWh

Battery_Capacity_kWh = df.hist(column='Battery_Capacity_kWh')
print(Battery_Capacity_kWh)

#top marcas vendidas
#las 5 marcas con más unidades vendidas

top_marcas = df.groupby("Brand")["Units_Sold"].sum().sort_values(ascending=False).head(5)

#Segmentación por tipo de vehículo
#tipo de vehículo (Vehicle_Type) más vendido en "North America"?

vehiculos_norte_america = df[df['Region'] == 'North America']
tipo_vehiculo_mas_vendido = vehiculos_norte_america['Vehicle_Type'].value_counts().head(5)
print(f"el tipo de vehiculo mas vendido es :{tipo_vehiculo_mas_vendido} ")


#grafico pastel
tipo_vehiculo_mas_vendido.plot(kind='pie')

